const TOKEN_KEY = "jwt";

export const login = (u) => {
  localStorage.setItem(TOKEN_KEY, u);
};

export const logout = () => {
  localStorage.removeItem(TOKEN_KEY);
};

export const isLogin = () => {
  if (localStorage.getItem(TOKEN_KEY)) {
    return true;
  }

  return false;
};
